        </div>
        <ul className='space-y-3'>
            {myTasks.map(myTasks => {
                return (
                <li key={myTasks} className='flex justify-between bg-white px-3 py-2 drop-shadow-lm rounded-lg'>
                    <span>{myTasks}</span>
                    <button onClick={() => deleteByValue(myTasks)} className='text-[#857E61] font-extrabold'>Delete</button>
                </li>
            )
            })}
        </ul>
    </div>
    </div>
  )
}

