    const [diceValue, setdiceValue] = useState(1);

    function randomNumberInRange(min, max) {
      // ðŸ‘‡ï¸ get number between min (inclusive) and max (inclusive)
      return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    const handleDice = () => {
      let get = randomNumberInRange(1, 6)
      while (diceValue === randomNumberInRange(1, 6)) {
        get = randomNumberInRange(1,6)
      }
      console.log()
        setdiceValue(randomNumberInRange(1, 6));
    };

  return (

    <div className='min-h-screen flex flex-col justify-center font-Poppins'>
        <div className='max-w-[180PX] w-full mx-auto h-[180px] mt-4 p-8 mb-12 bg-white rounded-3xl shadow-lm grid justify-center items-center animate-bounce grid-cols-2'>
          {Array.from({ length: diceValue }, () => (
            <div className=''>
              <div className='w-[20px] h-[20px] bg-black rounded-full mx-auto' />
            </div>
          ))}
        </div>
        <div className='text-center font-extrabold'>You got <span className='text-blue-500'>{diceValue}</span> !!!</div>
        {diceValue == 6 && <p className='text-blue-500 text-center font-extrabold'>You win</p>}
        <button onClick={handleDice} className='max-w-[180PX] w-full mx-auto mt-4 py-3 bg-white rounded-2xl shadow-lm grid justify-center items-center hover:scale-110 transition duration-500 hover:duration-500'>
          Click here !!!
        </button>

    </div>
  )
}

export default Todolist
