import React, {useState} from 'react'

function Todolist() {
    const [fruits, setFruits] = useState([
        "🍎 Apple",
        "🍊 Orange",
        "🍌 Banana",
        "🍇 Grapes",
      ])
      const deleteByValue = value => {
        setFruits(oldValues => {
          return oldValues.filter(fruit => fruit !== value)
        })
      }
      const AddByValue = value => {
        setFruits(current => [...current, value]);
      }
    // setArr(arr)
  return (
    <div className='min-h-screen flex flex-col justify-center font-Poppins'>
        <div className='max-w-[500PX] w-full mx-auto mt-4 bg-white p-8 rounded-3xl shadow-lm '>
            <h1 className='text-center text-[#857E61] text-2xl font-bold py-4'>My To Do App</h1>
            <div className='flex flex-row justify-center items-center space-x-5'>
                <input type="text" placeholder='Saisissez la tâche à ajouter' className='border w-full p-2 py-sm-3 rounded-xl outline-none placeholder:font-extrabold' required/>
                <button className="bg-[#857E61] font-bold whitespace-nowrap px-4 py-2 rounded-xl text-white text-center hover:scale-125 transition duration-500 hover:duration-500">Ajouter</button>
            </div>
            <ul>
        {fruits.map(fruit => {
          return (
            <li key={fruit}>
              <span>{fruit}</span>
              <button onClick={() => deleteByValue(fruit)}>Delete</button>
            </li>
          )
        })}
      </ul>
        </div>
    </div>
  )
}

export default Todolist
